import math

def geometric_mean(numbers):
    product = 1
    for num in numbers:
        product *= num
    return product ** (1/len(numbers))

def harmonic_mean(numbers):
    inverse_sum = sum(1 / num for num in numbers)
    return len(numbers) / inverse_sum

def quadratic_mean(numbers):
    square_sum = sum(num ** 2 for num in numbers)
    return math.sqrt(square_sum / len(numbers))

