import math

def min_cost_polygon_triangulation(points):
    n = len(points)
    if n < 3:
        return 0

    def cost(i, j, k):
        pi, pj, pk = points[i], points[j], points[k]
        return math.dist(pi, pj) + math.dist(pj, pk) + math.dist(pk, pi)

    dp = [[0] * n for _ in range(n)]

    for gap in range(2, n):
        for i in range(n - gap):
            j = i + gap
            dp[i][j] = float('inf')
            for k in range(i + 1, j):
                q = dp[i][k] + dp[k][j] + cost(i, j, k)
                if q < dp[i][j]:
                    dp[i][j] = q
                    print(f"Updating dp[{i}][{j}] with triangle ({i}, {j}, {k}): Cost = {q}")
                else:
                    print(f"Skipping triangle ({i}, {j}, {k}): No improvement")

    return dp[0][n - 1]

