import math

def heron_area(a, b, c):
    s = (a + b + c) / 2
    return math.sqrt(s * (s - a) * (s - b) * (s - c))

def inradius(a, b, c):
    area = heron_area(a, b, c)
    s = (a + b + c) / 2
    return area / s

