def generate_key(message, key_phrase):
    key = key_phrase
    while len(key) < len(message):
        key += key_phrase
    return key[:len(message)]

def encrypt(message, key, offset):
    encrypted = []
    for m, k in zip(message, key):
        if m.isalpha():
            shift = (ord(k) - ord('A') + offset) % 26
            enc_char = chr((ord(m) - ord('A') + shift) % 26 + ord('A'))
            encrypted.append(enc_char)
        else:
            encrypted.append(m)
    return ''.join(encrypted)

def decrypt(encrypted_message, key, offset):
    decrypted = []
    for e, k in zip(encrypted_message, key):
        if e.isalpha():
            shift = (ord(k) - ord('A') + offset) % 26
            dec_char = chr((ord(e) - ord('A') - shift) % 26 + ord('A'))
            decrypted.append(dec_char)
        else:
            decrypted.append(e)
    return ''.join(decrypted)

