def find_paths(grid, x, y, path):
    if x == len(grid) - 1 and y == len(grid[0]) - 1:
        return [path + [(x, y)]]
    if x >= len(grid) or y >= len(grid[0]):
        return []
    down_paths = find_paths(grid, x + 1, y, path + [(x, y)])
    right_paths = find_paths(grid, x, y + 1, path + [(x, y)])
    return down_paths + right_paths

