import re

def is_vowel(c):
    return c.lower() in 'aeiou'

def process_text_recursively(text, index=0, vowel_count=0, result=''):
    if index == len(text):
        return result, vowel_count
    char = text[index]
    if is_vowel(char):
        replacement = chr(ord(char) + 1)
        result += replacement
        vowel_count += 1
    else:
        result += char
    return process_text_recursively(text, index + 1, vowel_count, result)

def main():
    text = "The quick brown fox jumps over the lazy dog."
    processed_text, vowel_count = process_text_recursively(text)
    print(f"Original text:\n{text}\n")
    print(f"Processed text:\n{processed_text}\n")
    print(f"Number of vowels replaced: {vowel_count}")

