def knight_tour(n):
    moves = [(2, 1), (1, 2), (-1, 2), (-2, 1), 
             (-2, -1), (-1, -2), (1, -2), (2, -1)]

    def is_valid(x, y, board):
        return 0 <= x < n and 0 <= y < n and board[x][y] == -1

    def solve_tour(x, y, move, board):
        if move == n * n:
            return True
        for dx, dy in moves:
            nx, ny = x + dx, y + dy
            if is_valid(nx, ny, board):
                board[nx][ny] = move
                if solve_tour(nx, ny, move + 1, board):
                    return True
                board[nx][ny] = -1
        return False

    board = [[-1 for _ in range(n)] for _ in range(n)]
    board[0][0] = 0
    if not solve_tour(0, 0, 1, board):
        return "No solution exists"

    return board

