def chessboard_pattern(n):
    board = [['.' for _ in range(n)] for _ in range(n)]
    moves = [(2, 1), (1, 2), (-1, 2), (-2, 1), (-2, -1), (-1, -2), (1, -2), (2, -1)]
    x, y = 0, 0
    count = 1
    board[x][y] = count
    while count < n * n:
        for dx, dy in moves:
            nx, ny = x + dx, y + dy
            if 0 <= nx < n and 0 <= ny < n and board[nx][ny] == '.':
                count += 1
                board[nx][ny] = count
                x, y = nx, ny
                break
        else:
            break
    return board

