def generate_fibonacci_triangle(n):
    if n <= 0:
        return []

    triangle = [[1]]
    for i in range(1, n):
        row = [1]
        for j in range(1, i):
            row.append(triangle[i-1][j-1] + triangle[i-1][j])
        row.append(1)
        triangle.append(row)

    fib_sequence = [0, 1]
    while len(fib_sequence) < n * (n + 1) // 2:
        fib_sequence.append(fib_sequence[-1] + fib_sequence[-2])

    index = 0
    for i in range(n):
        for j in range(len(triangle[i])):
            triangle[i][j] = fib_sequence[index]
            index += 1

    return triangle

