def business_days_with_holidays(start_date, end_date, holidays):
    dp = [0] * ((end_date - start_date).days + 1)
    current_date = start_date
    day_index = 0

    while current_date <= end_date:
        if current_date.weekday() < 5 and current_date not in holidays:
            dp[day_index] = 1
        if day_index > 0:
            dp[day_index] += dp[day_index - 1]
        current_date += timedelta(days=1)
        day_index += 1

    return dp

