import math

def encode_message(message, shift):
    encoded = ""
    for char in message:
        if char.isalpha():
            shifted = ord(char) + shift
            if char.islower():
                if shifted > ord('z'):
                    shifted -= 26
                encoded += chr(shifted)
            elif char.isupper():
                if shifted > ord('Z'):
                    shifted -= 26
                encoded += chr(shifted)
        else:
            encoded += char
    return encoded

def decode_message(encoded, shift):
    return encode_message(encoded, -shift)

