def pell_numbers(n):
    if n == 0:
        return 0
    if n == 1:
        return 1
    pell = [0] * (n + 1)
    pell[0] = 0
    pell[1] = 1
    for i in range(2, n + 1):
        pell[i] = 2 * pell[i - 1] + pell[i - 2]
    return pell

def alternating_sum_pell(n):
    pell = pell_numbers(n)
    result = 0
    for i in range(n + 1):
        if i % 2 == 0:
            result += pell[i]
        else:
            result -= pell[i]
    return result

