import math

def min_cost_polygon_triangulation(points):
    n = len(points)
    if n < 3:
        return 0

    dp = [[0] * n for _ in range(n)]

    def distance_squared(i, j):
        return (points[i][0] - points[j][0]) ** 2 + (points[i][1] - points[j][1]) ** 2

    def cost(i, j, k):
        return math.sqrt(distance_squared(i, j)) + math.sqrt(distance_squared(j, k)) + math.sqrt(distance_squared(k, i))

    for gap in range(2, n):
        for i in range(n - gap):
            j = i + gap
            dp[i][j] = float('inf')
            for k in range(i + 1, j):
                dp[i][j] = min(dp[i][j], dp[i][k] + dp[k][j] + cost(i, j, k))

    return dp[0][n - 1]

