    while start_date <= end_date:
        if start_date in dp:
            day_of_week = dp[start_date]
        else:
            day_of_week = start_date.weekday()
            dp[start_date] = day_of_week

        day_counts[day_of_week] += 1
        start_date += delta

    return {day_names[i]: day_counts[i] for i in range(7)}

