import re

def recursive_find_words(text, pattern, index=0, found=None):
    if found is None:
        found = []
    match = re.search(pattern, text[index:])
    if match is None:
        return found
    start, end = match.span()
    found.append(text[index:][start:end])
    print(f"Found: {text[index:][start:end]} at index: {index + start}")
    return recursive_find_words(text, pattern, index + end, found)

def recursive_reverse_sentences(sentence_list, index=0):
    if index < len(sentence_list):
        reversed_sentence = ' '.join(sentence_list[index].split()[::-1])
        print(f"Reversing sentence {index}: {sentence_list[index]} -> {reversed_sentence}")
        return [reversed_sentence] + recursive_reverse_sentences(sentence_list, index + 1)
    return []

