import math

def generate_spiral_pattern(n):
    spiral = [[0]*n for _ in range(n)]
    x, y = n//2, n//2
    dx, dy = 0, -1
    step = 1

    for i in range(1, n*n + 1):
        if -n//2 < x <= n//2 and -n//2 < y <= n//2:
            spiral[y + n//2][x + n//2] = round(math.sqrt(x*x + y*y), 2)

        if x == step and y == -step:
            step += 1
        if x == step and y == step:
            dx, dy = 0, -1
        elif x == step and y == -step:
            dx, dy = -1, 0
        elif x == -step and y == -step:
            dx, dy = 0, 1
        elif x == -step and y == step:
            dx, dy = 1, 0

        x, y = x + dx, y + dy

    return spiral

def print_spiral_pattern(spiral):
    for row in spiral:
        print(' '.join(f"{val:6.2f}" for val in row))

