import re

def zigzag_pattern(text, num_rows):
    if num_rows == 1:
        return text

    rows = [''] * min(num_rows, len(text))
    current_row = 0
    going_down = False

    for char in text:
        rows[current_row] += char
        if current_row == 0 or current_row == num_rows - 1:
            going_down = not going_down
        current_row += 1 if going_down else -1

    return ''.join(rows)

def generate_text(pattern, length):
    sentence = re.sub(r'\W+', '', pattern).lower()
    sentence *= (length // len(sentence)) + 1
    return sentence[:length]

