def count_ways_to_sum(n):
    ways = [[0] * (n + 1) for _ in range(n + 1)]
    for i in range(n + 1):
        ways[i][0] = 1
        for j in range(1, i + 1):
            ways[i][j] = ways[i - 1][j] + (ways[i][j - i] if j - i >= 0 else 0)
    return ways[n][n]

