import re

def transform_data(data, depth=0):
    if isinstance(data, list):
        return [transform_data(item, depth + 1) for item in data]
    elif isinstance(data, str) and re.match(r'^\d+$', data):
        return int(data) * depth
    elif isinstance(data, str):
        return data[::-1] * depth
    return data

def recursive_transform(data):
    if isinstance(data, dict):
        return {k: recursive_transform(v) for k, v in data.items()}
    return transform_data(data)

