def solve_puzzle(grid, row, col, path):
    if row == len(grid) - 1 and col == len(grid[0]) - 1:
        return [path]
    paths = []
    if row + 1 < len(grid) and grid[row + 1][col] == 0:
        paths += solve_puzzle(grid, row + 1, col, path + 'D')
    if col + 1 < len(grid[0]) and grid[row][col + 1] == 0:
