import math

def encode_string(s):
    encoded = []
    for char in s:
        encoded.append((ord(char) * 3 + 7) % 256)
    return encoded

def dynamic_decode(encoded):
    n = len(encoded)
    dp = [[-1] * 256 for _ in range(n + 1)]
    dp[0][0] = 0

    for i in range(n):
        for j in range(256):
            if dp[i][j] != -1:
                for k in range(256):
                    if (k * 3 + 7) % 256 == encoded[i]:
                        dp[i + 1][k] = j

    result = []
    last = max(range(256), key=lambda x: dp[n][x])
    for i in range(n, 0, -1):
        result.append(chr(last))
        last = dp[i][last]

    return ''.join(reversed(result))

