def knight_moves(n, pos, visited=None):
    if visited is None:
        visited = set()
    if pos in visited or pos[0] < 0 or pos[0] >= n or pos[1] < 0 or pos[1] >= n:
        return 0
    visited.add(pos)
    if len(visited) == n * n:
        return 1
    moves = [(2, 1), (1, 2), (-1, 2), (-2, 1), 
             (-2, -1), (-1, -2), (1, -2), (2, -1)]
    total_paths = 0
    for move in moves:
        new_pos = (pos[0] + move[0], pos[1] + move[1])
        total_paths += knight_moves(n, new_pos, visited.copy())
    return total_paths

def knight_tours(n):
    start_pos = (0, 0)
    return knight_moves(n, start_pos)

