class FantasyGame:
    def __init__(self):
        self.characters = {
            "wizard": {"level": 5, "mana": 50, "health": 150},
            "knight": {"level": 7, "mana": 30, "health": 200},
            "archer": {"level": 6, "mana": 40, "health": 180},
        }
        self.battle_log = []

    def calculate_damage(self, attacker, defender):
        attack_power = self.characters[attacker]["level"] * 10
        defense_power = self.characters[defender]["level"] * 8
        damage = attack_power - defense_power
        return max(damage, 0)

    def perform_battle(self):
        characters = list(self.characters.keys())
        for i, attacker in enumerate(characters):
            defender = characters[(i + 1) % len(characters)]
            damage = self.calculate_damage(attacker, defender)
            self.characters[defender]["health"] -= damage
            self.battle_log.append(f"{attacker} attacks {defender} for {damage} damage.")

    def print_battle_summary(self):
        for entry in self.battle_log:
            print(entry)
        print("\nFinal Health Status:")
