import math

def generate_wave_pattern(length, amplitude, frequency):
    pattern = []
    for i in range(length):
        value = math.sin(2 * math.pi * frequency * i / length) * amplitude
        pattern.append(value)
    return pattern

def analyze_wave_pattern(pattern):
    peaks = []
    troughs = []
    for i in range(1, len(pattern) - 1):
        if pattern[i] > pattern[i - 1] and pattern[i] > pattern[i + 1]:
            peaks.append((i, pattern[i]))
        elif pattern[i] < pattern[i - 1] and pattern[i] < pattern[i + 1]:
            troughs.append((i, pattern[i]))
    return peaks, troughs

length = 100
amplitude = 10
frequency = 3

wave_pattern = generate_wave_pattern(length, amplitude, frequency)
peaks, troughs = analyze_wave_pattern(wave_pattern)

for index, value in peaks:
    print(f"Peak at index {index}: {value:.2f}")

for index, value in troughs:
    print(f"Trough at index {index}: {value:.2f}")

from datetime import datetime, timedelta

def recursive_date_adder(start_date, days_to_add):
    if days_to_add == 0:
        return start_date
    return recursive_date_adder(start_date + timedelta(days=1), days_to_add - 1)

def generate_dates(start_date, days_to_add, frequency):
    if frequency <= 0:
        return []
    result = []
    for i in range(0, days_to_add, frequency):
        new_date = recursive_date_adder(start_date, i)
        result.append(new_date.strftime("%Y-%m-%d"))
    return result

