import re

def encode_string(s):
    encoded = []
    for char in s:
        if char.isalpha():
            shift = 3
            if char.islower():
                base = ord('a')
            else:
                base = ord('A')
            encoded.append(chr((ord(char) - base + shift) % 26 + base))
        else:
            encoded.append(char)
    return ''.join(encoded)

def decode_string(encoded):
    decoded = []
    for char in encoded:
        if char.isalpha():
            shift = 3
            if char.islower():
                base = ord('a')
            else:
                base = ord('A')
            decoded.append(chr((ord(char) - base - shift + 26) % 26 + base))
        else:
            decoded.append(char)
    return ''.join(decoded)

def hide_data(encoded_message):
    reversed_message = encoded_message[::-1]
    words = re.findall(r'\b\w+\b', reversed_message)
    hidden = ' '.join(word[::-1] for word in words)
    return hidden

original_message = "Intermediate level encryption and encoding."
print("Original Message:", original_message)

encoded_message = encode_string(original_message)
print("Encoded Message:", encoded_message)

hidden_message = hide_data(encoded_message)
print("Hidden Message:", hidden_message)

decoded_hidden_message = hide_data(hidden_message)
decoded_message = decode_string(decoded_hidden_message)
print("Decoded Hidden Message:", decoded_hidden_message)
print("Decoded Message:", decoded_message)

import math

def generate_spiral(n):
    grid = [[' ' for _ in range(n)] for _ in range(n)]
    x, y = n // 2, n // 2
    step = 1
    direction = 0
    for i in range(1, n * n + 1):
        grid[y][x] = f"{i % 10}"
        if direction == 0:
            x += 1
        elif direction == 1:
            y += 1
        elif direction == 2:
            x -= 1
        elif direction == 3:
            y -= 1
        if i == step**2:
            direction = (direction + 1) % 4
            if direction == 0 or direction == 2:
                step += 1
    return grid

def print_spiral(grid):
    for row in grid:
        print(''.join(row))

n = 11
spiral = generate_spiral(n)
print_spiral(spiral)

