import math

def transform_data(data):
    transformed = []
    for i, value in enumerate(data):
        if i % 2 == 0:
            transformed.append(math.sin(value))
        else:
            transformed.append(math.cos(value))
    return transformed

def process():
    data = [i * 0.1 for i in range(100)]
    transformed_data = transform_data(data)
    results = []
    for i in range(0, len(transformed_data), 10):
        batch = transformed_data[i:i+10]
        result = math.prod(batch)
        results.append(result)
    return results

