import math

def simulate_dice_rolls():
    roll_outcomes = [0] * 13
    for i in range(1, 7):
        for j in range(1, 7):
            roll_outcomes[i + j] += 1

    total_rolls = 6 * 6
    roll_probabilities = [outcome / total_rolls for outcome in roll_outcomes]

    return roll_probabilities

def calculate_score_difference():
    probabilities = simulate_dice_rolls()
    scores = [0] * 13
    for point_value in range(2, 13):
        scores[point_value] = int(probabilities[point_value] * 100)

    score_difference = []
    for i in range(2, 12):
        difference = abs(scores[i] - scores[i + 1])
        score_difference.append((i, i + 1, difference))

    return score_difference

def display_results():
    print("Score Differences Between Consecutive Dice Rolls:")
    differences = calculate_score_difference()
    for roll_pair in differences:
        print(f"Rolls {roll_pair[0]} and {roll_pair[1]}: Difference = {roll_pair[2]}")

display_results()
